/**
 * This class stores data on application level.
 */
export default class ApplicationData {
    _screen: any;
    _application: any;
    _saveScreenFunction: Function;
    constructor(screenData: any, application: any);
    /**
     * This field stores the dynamicAttributes we entered on the manifest.json
     */
    set application(data: any);
    get application(): any;
    /**
     * This field holds the elementData class by topic and key
     */
    set screen(data: any);
    get screen(): any;
    clearScreenData(): void;
    getScreenData(key: string): any;
    setScreenData(key: string, value: Object): void;
    /**
     * This method gets the elementData stored by the topic and key
     * @param topic
     * @param key
     * @returns the elementData
     */
    getScreenTopicData(topic: string, key: string): any;
    /**
     * This method sets the elementData by the topic and key
     * @param topic
     * @param key
     * @param value - the object to set
     */
    setScreenTopicData(topic: string, key: string, value: Object): void;
    /**
     * This method deletes all the data stored on the given topic.
     * @param topic
     */
    deleteScreenTopic(topic: string): void;
    /**
     * This method deleted the data stored on the given topic and key.
     * @param topic
     * @param key
     */
    deleteScreenTopicData(topic: string, key: string): void;
}
