import { GenericResult } from "@jacada-jbot/base/include/Model/GenericResult";
import { WebCustomControlCoverageHandler } from "./WebCustomControlCoverageHandler";
/**
 * Coverage handler for a select
 */
export default class SelectWebControlCoverageHandler extends WebCustomControlCoverageHandler {
    type: string;
    startRecording(): boolean;
    subscribeToSelectionChanged(): void;
    /**
     * This method sets the selected item with the given value by first using filter function to find the item, then sets the property selected
     * and in the end, dispatching change event on the node.
     * @param item - the item to select
     * @returns a GenericResult struct
     */
    setSelectedItem(item: string): GenericResult;
    /**
     * This method gets the property selectedIndex of the node.
     * @returns a GenericResult struct
     */
    getSelectedIndex(): GenericResult;
    getItems(): GenericResult;
    /**
     * This method sets the property selectedIndex with the given index.
     * @param index - the index number to set
     * @returns a GenericResult struct
     */
    setSelectedIndex(index: number): GenericResult;
    /**
     * This method gets the value of the node.
     * @returns a GenericResult struct
     */
    getSelectedItem(): GenericResult;
}
