import { GenericResult } from "@jacada-jbot/base/include/Model/GenericResult";
import { WebCustomControlCoverageHandler } from "./WebCustomControlCoverageHandler";
/**
 * Coverage handler for a textarea
 */
export default class TextAreaWebControlCoverageHandler extends WebCustomControlCoverageHandler {
    type: string;
    node: JQuery<HTMLTextAreaElement>;
    startRecording(): boolean;
    subscribeToTextChanged(): void;
    /**
     * This method gets the value of the node.
     * @returns a GenericResult struct
     */
    getText(): GenericResult;
    /**
     * This method sets the value with the given text by dispatching change event on the node.
     * @param text - the text to set
     * @returns a GenericResult struct
     */
    setText(text: string): GenericResult;
}
