import CoverageHandler from "@jacada-jbot/base/include/CoverageHandlers/CoverageHandler";
import ApplicationCoverageHandler from "@jacada-jbot/base/include/CoverageHandlers/ApplicationCoverageHandler";
import RepositoryHandler from "@jacada-jbot/base/include/Model/RepositoryHandler";
import ScreenCoverageHandler from "@jacada-jbot/base/include/CoverageHandlers/ScreenCoverageHandler";
import { GenericResult } from "@jacada-jbot/base/include/Model/GenericResult";
import ListeningInfo from "@jacada-jbot/base/include/Model/ListeningInfo";
import ExecutionLogicalAction from "@jacada-jbot/base/include/Model/ExecutionLogicalAction";
/**
 * Coverage handler for a application
 */
export default class WebApplicationCoverageHandler extends ApplicationCoverageHandler {
    protected getDefaultScreenCoverageHandler(applicationCoverageHandler: CoverageHandler, screenRepositoryHandler: RepositoryHandler): ScreenCoverageHandler;
    startListening(): GenericResult;
    /**
     * Starts listening on iFrames
     */
    startListeningOnFrame(): void;
    /**
     * Start listening on specific element in iFrame
     * @param listeningInfo
     */
    startListeningOnFrameElement(listeningInfo: ListeningInfo): void;
    private startListeningOnScreen;
    stopListening(): GenericResult;
    stopRecording(): GenericResult;
    stopCapturing(): GenericResult;
    /**
     * This method validates the element path - before execution
     * @param lElementPathId - the element path id
     * @param lAction - the action to execute
     * @returns a GenericResult
     */
    validateElementPath(lElementPathId: string, lAction: ExecutionLogicalAction): Promise<GenericResult>;
}
