import WebApplicationCoverageHandler from '../CoverageHandlers/Web/WebApplicationCoverageHandler';
import { IRepositoryNode } from './interfaces';
import WebLogger from '../Utils/WebLogger';
/**
 * Application context holds set of property to coverage an application
 */
export default class ApplicationContext {
    private logger;
    /**
     * If not defined, we are the top frame
     */
    myFrameGuid: string;
    appRepositoryNode: IRepositoryNode;
    currentApp: WebApplicationCoverageHandler;
    applicationData: string;
    applicationName: string;
    /**
     *
     */
    constructor(logger: WebLogger);
    /**
     * Create application coverage handler globally
     */
    createAppObject(): void;
}
